mapper.select:
select member_id
from member
where member_name = #{findName}
and email = #{findEmail}
and phone = #{findPhone}

mapper.update:
update member
set member_pwd = #{tempPwd}
where member_id = #{findId} 
and member_name = #{findName}
and email = #{findEmail}
and phone = #{findPhone}

update member
set status = 'N'
where member_no = #{memberNo}

mapper.select:
select count(*)
from member
where member_Id = #{id}

select count(*)
from member
where nickName = #{nickName}

mapper.update:
update member
set status = #{status}
where member_no = #{memberNo}

update member
set isAdmin = #{isAdmin}
where member_no = #{memberNo}
