mapper.select:
select member_id
from member
where member_name = #{findName}
and email = #{findEmail}
and phone = #{findPhone}

mapper.update:
update member
set member_pwd = #{tempPwd}
where member_id = #{findId} 
and member_name = #{findName}
and email = #{findEmail}
and phone = #{findPhone}

update member
set status = 'N'
where member_no = #{memberNo}

update member
set member_name = #{memberName},
	nickname = #{nickName},
	email = #{email},
	phone = #{phone}
where member_no = #{memberNo}

update member
set status = #{status}
where member_no = #{memberNo}
